package Java12_1;

public class Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 10;
		int j = 20;
		
		//i와 j를 더하고 빼고 곱하라
		//System.out.println(덧셈 뺄쌤 나타내기);
	
		int i_1= i+j;
		int i_2= i-j;
		
		double i_3= i/j;
		int i_4 = i*j;
		double d = (double)(i)/(double)(j);
		System.out.println("덧셈: "+i_1);
		System.out.println("뺄셈: "+i_2);
		System.out.println("나누기: "+i_3);
		System.out.println("곱하기: "+i_4);
		System.out.println(d);
	
		
	}

}
