package Java12_4;

public class Forpractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str = "Covid";
		String str1_1 = "안녕하세요";
		int i = 1;

		for (;;) {             
			System.out.println(str + i);
			i++;
			if (i == 20) {
				break;
			}
		}
		for (int j = 0; j<=20; j++) {
			System.out.println(str1_1+j);
		}

	}

}
